// --- DOCUMENTO MAESTRO ---
export const CarnetDocument = ({ miembros }) => {
    // 8 carns por pgina (2 columnas x 4 filas)
    const itemsPorPagina = 8;
    const paginas = [];

    for (let i = 0; i < miembros.length; i += itemsPorPagina) {
        paginas.push(miembros.slice(i, i + itemsPorPagina));
    }

    return (
        <Document>
            {paginas.map((grupo, index) => (
                <React.Fragment key={index}>
                    {/* PGINA IMPAR: FRENTES */}
                    <Page size="LETTER" style={styles.page}>
                        {grupo.map((miembro) => (
                            <CarnetFrente key={miembro.id} miembro={miembro} />
                        ))}
                    </Page>

                    {/* PGINA PAR: REVERSOS */}
                    <Page size="LETTER" style={styles.page}>
                        {grupo.map((_, i) => (
                            <CarnetReverso key={i} />
                        ))}
                    </Page>
                </React.Fragment>
            ))}
        </Document>
    );
};